select smn_inventario.smn_rol.smn_rol_id as id, smn_base.smn_auxiliar.aux_nombres ||' - '|| smn_base.smn_auxiliar.aux_apellidos as item from smn_inventario.smn_rol inner join smn_base.smn_usuarios on smn_base.smn_usuarios.smn_usuarios_id = smn_inventario.smn_rol.smn_usuarios_rf inner join smn_base.smn_auxiliar on smn_base.smn_auxiliar.smn_auxiliar_id = smn_base.smn_usuarios.smn_auxiliar_rf where smn_inventario.smn_rol.rol_tipo='IN'